<script setup lang="ts">
import { ref } from "vue";
import { waitFor } from "@/utils";
import { tg } from "@/api/telegram";
import constants from "@/constants";

// composables
import preloadAssets from "@/composables/preloadAssets";

// stores
import { useMainStore } from "@/store/main";
import { useSoundStore } from "@/store/sound";

const { botName } = constants;

const mainStore = useMainStore();
const soundStore = useSoundStore();
const { startApp, initialFetch } = mainStore;
const { addSound, playSound } = soundStore;

const loading = ref(true);
const errors = ref([]);

// const onClick = () => {
//   if (!loading.value && !errors.value.length) {
//     startApp();
//   }
// };

tg?.ready();

tg?.disableVerticalSwipes();

tg?.expand();

console.log("tg:", tg);

Promise.allSettled([
  initialFetch({
    botName,
    tg: {
      timezone: Intl.DateTimeFormat().resolvedOptions().timeZone,
      rawInitData: tg.initData,
      initData: tg.initDataUnsafe,
      version: tg.version,
      platform: tg.platform,
    },
  }),
  preloadAssets({ addSound }),
  waitFor(2000),
]).then((data) => {
  const preloadErrors = [data[0], ...data[1].value].filter((p) => p.status === "rejected");

  if (preloadErrors.length) {
    errors.value = preloadErrors.map((err) => err.reason);
    return;
  }

  loading.value = false;

  startApp();
  // playSound("soundtrack");
});
</script>
